<?phpif (!defined('IN_IA')) {	exit('Access Denied');} if (!class_exists('DesignerModel')) {	class DesignerModel extends PluginModel {		public function getPage($type = 1) {			global $_W, $_GPC;			$page = pdo_fetch("SELECT * FROM " . tablename('ewei_shop_designer') . " WHERE uniacid= :uniacid and pagetype=:type and setdefault=:default", array(':uniacid' => $_W['uniacid'], ':type' => $type, ':default' => '1'));			if (empty($page)) {				return false;			} 			return $this -> getData($page);		} 		public function change(&$d, $cdata) {			$d[$b['k1']][$b['k2']]['name'] = $cdata['title'];			$d[$b['k1']][$b['k2']]['priceold'] = $cdata['productprice'];			$d[$b['k1']][$b['k2']]['pricenow'] = $cdata['marketprice'];			$d[$b['k1']][$b['k2']]['img'] = $cdata['thumb'];		} 		public function getData($page) {			global $_W;			$data = htmlspecialchars_decode($page['datas']);			$d = json_decode($data, true);			$goodsids = array();			foreach ($d as $k1 => &$dd) {				if ($dd['temp'] == 'goods') {					foreach ($dd['data'] as $k2 => $ddd) {						$goodsids[] = array('id' => $ddd['goodid'], 'k1' => $k1, 'k2' => $k2);					} 				} elseif ($dd['temp'] == 'richtext') {					$dd['content'] = $this -> unescape($dd['content']);				} 			} 			unset($dd);			$arr = array();			foreach($goodsids as $a) {				$arr[] = $a['id'];			} 			if (count($arr) > 0) {				$goodinfos = pdo_fetchall("SELECT id,title,productprice,marketprice,thumb FROM " . tablename('ewei_shop_goods') . " WHERE id in ( " . implode(',', $arr) . ") and uniacid= :uniacid ", array(':uniacid' => $_W['uniacid']), 'id');				$goodinfos = set_medias($goodinfos, 'thumb');				foreach ($d as $k1 => &$dd) {					if ($dd['temp'] == 'goods') {						foreach ($dd['data'] as $k2 => &$ddd) {							$cdata = $goodinfos[$ddd['goodid']];							$ddd['name'] = $cdata['title'];							$ddd['priceold'] = $cdata['productprice'];							$ddd['pricenow'] = $cdata['marketprice'];							$ddd['img'] = $cdata['thumb'];						} 						unset($ddd);					} 				} 				unset($dd);			} 			$data = json_encode($d);			$data = rtrim($data, "]");			$data = ltrim($data, "[");			$pageinfo = htmlspecialchars_decode($page['pageinfo']);			$p = json_decode($pageinfo, true);			$page_title = empty($p[0]['params']['title'])?"未设置页面标题":$p[0]['params']['title'];			$page_desc = empty($p[0]['params']['desc'])?"未设置页面简介":$p[0]['params']['desc'];			$page_img = empty($p[0]['params']['img'])?"":tomedia($p[0]['params']['img']);			$page_keyword = empty($p[0]['params']['kw'])?"":$p[0]['params']['kw'];			$shopset = m('common') -> getSysset('shop');			$system = array('shopname' => $shopset['name'], 'shoplogo' => tomedia($shopset['logo']));			$system = json_encode($system);			return array('page' => $page, 'data' => $data, 'share' => array('title' => $page_title, 'desc' => $page_desc, 'imgUrl' => $page_img), 'footermenu' => intval($p[0]['params']['footer']), 'system' => $system);		} 		public function unescape($str) {			$ret = '';			$len = strlen($str);			for ($i = 0;$i < $len;$i ++) {				if ($str[$i] == '%' && $str[$i + 1] == 'u') {					$val = hexdec(substr($str, $i + 2, 4));					if ($val < 0x7f)$ret .= chr($val);					else if ($val < 0x800)$ret .= chr(0xc0 | ($val >> 6)) . chr(0x80 | ($val &0x3f));					else $ret .= chr(0xe0 | ($val >> 12)) . chr(0x80 | (($val >> 6)&0x3f)) . chr(0x80 | ($val &0x3f));					$i += 5;				} else if ($str[$i] == '%') {					$ret .= urldecode(substr($str, $i, 3));					$i += 2;				} else $ret .= $str[$i];			} 			return $ret;		} 		public function perms() {			return array('designer' => array('text' => $this -> getName(), 'isplugin' => true, 'child' => array('page' => array('text' => '页面设置', 'view' => '浏览', 'edit' => '添加修改-log', 'delete' => '删除-log', 'setdefault' => '设置默认-log'))));		} 	} } 